import { Box, Heading, Text } from '@primer/react';

export function DataCard({ data, description, title }) {
  return (
    <Box
      sx={{
        p: 3,
        borderRadius: 2,
        boxShadow: 1,
        mb: 3,
        border: '1px solid',
        borderColor: 'border.default',
      }}
    >
      <Heading>{title}</Heading>
      <Text>{data}</Text>
      <Text>{description}</Text>
    </Box>
  );
}
