import { Box, Text } from '@primer/react';
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Data } from '../../../data/Data';

export function LineChart() {
  const labels = [
    {
      name: 'Total requests',
      color: 'success.emphasis',
    },
    {
      name: 'Rate limited requests',
      color: 'severe.emphasis',
    },
  ];

  const getOrCreateTooltip = (chart) => {
    let tooltipEl = chart.canvas.parentNode.querySelector('div');

    if (!tooltipEl) {
      tooltipEl = document.createElement('div');
      tooltipEl.style.background = 'rgba(0, 0, 0, 0.7)';
      tooltipEl.style.borderRadius = '3px';
      tooltipEl.style.color = 'white';
      tooltipEl.style.opacity = 1;
      tooltipEl.style.pointerEvents = 'none';
      tooltipEl.style.position = 'absolute';
      tooltipEl.style.transform = 'translate(-50%, 0)';
      tooltipEl.style.transition = 'all .1s ease';

      const table = document.createElement('table');
      table.style.margin = '0px';

      tooltipEl.appendChild(table);
      chart.canvas.parentNode.appendChild(tooltipEl);
    }

    return tooltipEl;
  };

  const externalTooltipHandler = (context) => {
    // Tooltip Element
    const { chart, tooltip } = context;
    const tooltipEl = getOrCreateTooltip(chart);

    // Hide if no tooltip
    if (tooltip.opacity === 0) {
      tooltipEl.style.opacity = 0;
      return;
    }

    // Set Text
    if (tooltip.body) {
      const titleLines = tooltip.title || [];
      const bodyLines = tooltip.body.map((b) => b.lines);

      const tableHead = document.createElement('thead');

      titleLines.forEach((title) => {
        const tr = document.createElement('tr');
        tr.style.borderWidth = 0;

        const th = document.createElement('th');
        th.style.borderWidth = 0;
        const text = document.createTextNode(title);

        th.appendChild(text);
        tr.appendChild(th);
        tableHead.appendChild(tr);
      });

      const tableBody = document.createElement('tbody');
      bodyLines.forEach((body, i) => {
        const colors = tooltip.labelColors[i];

        const span = document.createElement('span');
        span.style.background = colors.backgroundColor;
        span.style.borderColor = colors.borderColor;
        span.style.borderWidth = '2px';
        span.style.marginRight = '10px';
        span.style.height = '10px';
        span.style.width = '10px';
        span.style.display = 'inline-block';

        const tr = document.createElement('tr');
        tr.style.backgroundColor = 'inherit';
        tr.style.borderWidth = 0;

        const td = document.createElement('td');
        td.style.borderWidth = 0;

        const text = document.createTextNode(body);

        td.appendChild(span);
        td.appendChild(text);
        tr.appendChild(td);
        tableBody.appendChild(tr);
      });

      const tableRoot = tooltipEl.querySelector('table');

      // Remove old children
      while (tableRoot.firstChild) {
        tableRoot.firstChild.remove();
      }

      // Add new children
      tableRoot.appendChild(tableHead);
      tableRoot.appendChild(tableBody);
    }

    const { offsetLeft: positionX, offsetTop: positionY } = chart.canvas;

    // Display, position, and set styles for font
    tooltipEl.style.opacity = 1;
    tooltipEl.style.left = positionX + tooltip.caretX + 'px';
    tooltipEl.style.top = positionY + tooltip.caretY + 'px';
    tooltipEl.style.font = tooltip.options.bodyFont.string;
    tooltipEl.style.padding =
      tooltip.options.padding + 'px ' + tooltip.options.padding + 'px';
  };

  const data = {
    labels: Data.map((data) => data.time),
    datasets: [
      {
        label: 'Requests',
        data: Data.map((data) => data.requests),
        fill: true,
        backgroundColor: 'rgba(45, 164, 78, 0.1)',
        borderColor: '#2DA44E',
        borderWidth: 1,
        backgroundColor: (context) => {
          const ctx = context.chart.ctx;
          const gradient = ctx.createLinearGradient(0, 0, 0, 250);
          gradient.addColorStop(0, 'rgba(218,251,225,1)');
          gradient.addColorStop(1, 'rgba(218,251,225,0.0)');
          return gradient;
        },
        pointHoverBackgroundColor: '#2DA44E',
      },
      {
        label: 'Rate limited requests',
        data: Data.map((data) => data.requestsFailed),
        fill: true,
        backgroundColor: 'rgba(45, 164, 78, 0.1)',
        borderColor: '#E16F24',
        borderWidth: 1,
        backgroundColor: (context) => {
          const ctx = context.chart.ctx;
          const gradient = ctx.createLinearGradient(0, 0, 0, 400);
          gradient.addColorStop(0, 'rgba(255,241,229,1)');
          gradient.addColorStop(1, 'rgba(255,241,229,0.0)');
          return gradient;
        },
        pointHoverBackgroundColor: '#E16F24',
      },
    ],
  };

  return (
    <Box
      sx={{
        position: 'relative',
        height: '300px',
        width: '100%',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          gap: 3,
          pl: 0,
        }}
      >
        {labels.map((label) => (
          <Box key={label.name}>
            <Box
              sx={{
                height: '8px',
                width: '8px',
                borderRadius: '100%',
                backgroundColor: label.color,
                display: 'inline-block',
                mr: 2,
              }}
            />
            <Text
              sx={{
                fontSize: '12px',
              }}
            >
              {label.name}
            </Text>
          </Box>
        ))}
      </Box>
      <Line
        data={data}
        options={{
          maintainAspectRatio: false,
          height: '340px',
          responsive: true,
          interaction: {
            intersect: false,
            mode: 'nearest',
            axis: 'xy',
          },
          elements: {
            point: {
              radius: 0,
            },
          },
          scales: {
            y: {
              beginAtZero: true,
              ticks: {
                callback: (val) => {
                  return val / 1000 + 'k';
                },
                padding: 12,
              },
              border: {
                dash: [3, 3],
              },
            },
            x: {
              beginAtZero: true,
              border: {
                dash: [3, 3],
              },
            },
          },
          plugins: {
            title: {
              display: false,
              text: 'Total requests made by this organization',
            },
            legend: {
              display: false,
              position: 'top',
              align: 'start',
              labels: {
                boxWidth: 8,
                boxHeight: 8,
                borderRadius: '100px',
                useBorderRadius: true,
                pointStyle: 'circle',
                borderRadius: 4,
              },
            },
            datalabels: {
              formatter: (value) => {
                if (value > 1000) {
                  return value / 1000 + 'k';
                }
                return value;
              },
            },
            tooltip: {
              enabled: false,
              position: 'nearest',
              external: externalTooltipHandler,
            },
          },
        }}
      />
    </Box>
  );
}
