import { useState } from 'react';
import {
  CheckIcon,
  SearchIcon,
  TriangleDownIcon,
  XIcon,
} from '@primer/octicons-react';
import {
  Box,
  Button,
  Heading,
  IconButton,
  Link,
  NavList,
  Text,
  TextInput,
} from '@primer/react';

export function SelectHeader() {
  const [selected, setSelected] = useState('All usage');
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');

  const costCenters = [
    'All usage',
    'Actions',
    'Engineering',
    'Marketplace',
    'Packages',
    'Primer',
    'Sponsors',
  ];

  const filteredCostCenters = costCenters.filter((center) =>
    center.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box
      sx={{
        width: '100%',
      }}
    >
      <Box
        sx={{
          borderBottom: '1px solid',
          borderColor: 'border.default',
          alignSelf: 'flex-start',
          pb: 3,
        }}
      >
        <button
          onClick={() => setIsOpen(!isOpen)}
          style={{
            border: 'none',
            background: 'transparent',
            boxShadow: 'none',
            display: 'flex',
            flexDirection: 'row',
            px: 2,
            ml: -2,
            cursor: 'pointer',
            alignItems: 'center',
            fontFamily: 'inherit',
            span: {
              display: 'flex',
              alignItems: 'center',
              gap: 1,
            },
            _hover: {
              bg: 'transparent',
            },
            _focus: {
              bg: 'transparent',
            },
          }}
        >
          <Heading
            as='h1'
            sx={{
              fontSize: '4',
              fontWeight: 500,
              color: 'fg.default',
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              letterSpacing: '-0.5px',
            }}
          >
            {selected}

            <TriangleDownIcon
              sx={{
                color: 'fg.default',
                bg: 'red',
              }}
              size={20}
            />
          </Heading>
        </button>

        {isOpen && (
          <Box
            sx={{
              mt: 1,
              position: 'absolute',
              bg: 'canvas.default',
              minWidth: '320px',
              maxWidth: '320px',
              border: '1px solid',
              borderColor: 'border.default',
              fontSize: 1,
              boxShadow:
                '0 1px 3px 0 rgba(31, 35, 40, 0.12), 0 8px 24px 0 rgba(66, 74, 83, 0.12)',
              borderRadius: 12,
            }}
          >
            <Box
              sx={{
                p: 3,
                pb: 2,
              }}
            >
              <Heading sx={{ fontSize: 1 }}>Select cost center</Heading>
              <Text
                sx={{
                  color: 'fg.muted',
                  fontSize: '12px',
                }}
              >
                View usage for individual cost centers
              </Text>
              <IconButton
                onClick={() => setIsOpen(false)}
                icon={XIcon}
                sx={{
                  color: 'fg.muted',
                  bg: 'transparent',
                  border: 'none',
                  boxShadow: 'none',
                  position: 'absolute',
                  top: 2,
                  right: 2,
                }}
              />
            </Box>
            <Box
              sx={{
                p: 2,
                pt: 0,
                borderBottom: '1px solid',
                borderColor: 'border.default',
              }}
            >
              <TextInput
                leadingVisual={SearchIcon}
                sx={{
                  width: '100%',
                  color: 'fg.muted',
                }}
                placeholder='Search'
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </Box>
            <Box
              sx={{
                minHeight: '150px',
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <NavList
                sx={{
                  width: '100%',
                }}
              >
                {filteredCostCenters.length >= 1 ? (
                  filteredCostCenters.map((center) => (
                    <NavList.Item
                      onClick={() => {
                        setSelected(center);
                        setIsOpen(false);
                      }}
                      key={center}
                    >
                      <NavList.LeadingVisual
                        sx={{
                          opacity: center === selected ? '1' : '0',
                        }}
                      >
                        <CheckIcon size={16} />
                      </NavList.LeadingVisual>
                      {center}
                    </NavList.Item>
                  ))
                ) : (
                  <Box
                    sx={{
                      display: 'flex',
                      flexDirection: 'column',
                      justifyContent: 'center',
                      alignItems: 'center',
                      height: '100%',
                      bg: 'pink',
                      m: 4,
                    }}
                  >
                    <Heading
                      sx={{
                        fontSize: 1,
                        color: 'fg.default',
                        textAlign: 'center',
                      }}
                    >
                      No cost center found
                    </Heading>
                    <Text
                      sx={{
                        fontSize: 1,
                        color: 'fg.muted',
                        textAlign: 'center',
                      }}
                    >
                      {/* Try searching for a different cost center or add a new one */}
                    </Text>
                  </Box>
                )}
              </NavList>
            </Box>
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'center',
                p: 2,
                borderTop: '1px solid',
                borderColor: 'border.default',
              }}
            >
              <Button
                sx={{
                  cursor: 'pointer',
                  width: '100%',
                }}
                variant='invisible'
              >
                Manage cost centers
              </Button>
            </Box>
          </Box>
        )}

        <Text
          sx={{
            color: 'fg.muted',
            fontSize: '1',
          }}
        >
          Includes amounts spent for organizations and repositories across all
          services.{' '}
          <Link href='/'>
            <Text
              as='span'
              sx={{
                color: 'accent.fg',
              }}
            >
              View current and past pricing information.
            </Text>
          </Link>
        </Text>
      </Box>
      <Box
        sx={{
          p: 3,
          border: '1px solid',
          borderColor: 'border.default',
          borderRadius: 6,
          mt: 3,
        }}
      >
        <Text
          sx={{
            fontWeight: 'bold',
            display: 'block',
          }}
        >
          {selected}
        </Text>
        <Text
          sx={{
            fontSize: 1,
            color: 'fg.muted',
          }}
        >
          Feb 1 - Feb 28, 2024
        </Text>
        <Box
          sx={{
            mt: 3,
            height: '200px',
            bg: 'canvas.subtle',
            borderRadius: 6,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Text
            sx={{
              fontSize: 1,
              color: 'fg.muted',
            }}
          >
            Chart goes here
          </Text>
        </Box>
      </Box>
    </Box>
  );
}
