import { Box, Heading, Text } from '@primer/react';

export function TrendCard({ title, data, description }) {
  return (
    <Box
      sx={{
        p: 3,
        borderRadius: 2,
        boxShadow: 1,
        mb: 3,
        border: '1px solid',
        borderColor: 'border.default',
        width: '100%',
      }}
    >
      <Heading fontSize={2}>{title}</Heading>
      <Text
        sx={{
          fontSize: 1,
          color: 'text.secondary',
          mb: 2,
        }}
      >
        {description}
      </Text>
      {data.map((item, index) => (
        <Box
          key={index}
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            mb: 2,
          }}
        >
          <Text>{item.value}</Text>
          <Text>{item.count}</Text>
        </Box>
      ))}
    </Box>
  );
}
