import { Box } from '@primer/react';
import React from 'react';
import { Line } from 'react-chartjs-2';
import { Data } from '../../../data/Data';

export function LineChart() {
  const data = {
    labels: Data.map((data) => data.time),
    datasets: [
      {
        label: 'Requests',
        data: Data.map((data) => data.requests),
        fill: true,
        backgroundColor: 'rgba(45, 164, 78, 0.1)',
        borderColor: '#2DA44E',
        borderWidth: 1.5,
        backgroundColor: (context) => {
          const ctx = context.chart.ctx;
          const gradient = ctx.createLinearGradient(0, 0, 0, 200);
          gradient.addColorStop(0, 'rgba(218,251,225,1)');
          gradient.addColorStop(1, 'rgba(218,251,225,0.0)');
          return gradient;
        },
      },
    ],
  };

  return (
    <Box
      sx={{
        position: 'relative',
        height: '320px',
        width: '100%',
      }}
    >
      <Line
        data={data}
        options={{
          maintainAspectRatio: false,
          height: '340px',
          responsive: true,
          elements: {
            point: {
              radius: 0,
            },
          },
          scales: {
            y: {
              beginAtZero: true,
              ticks: {
                callback: (val) => {
                  return val / 1000 + 'k';
                },
                padding: 12,
              },
              border: {
                dash: [3, 3],
              },
            },
            x: {
              border: {
                dash: [3, 3],
              },
            },
          },
          plugins: {
            title: {
              display: false,
              text: 'Total requests made by this organization',
            },
            legend: {
              display: false,
            },
            datalabels: {
              formatter: (value, context) => {
                if (value > 1000) {
                  return value / 1000 + 'k';
                }
                return value;
              },
            },
          },
        }}
      />
    </Box>
  );
}
