import { TriangleDownIcon } from '@primer/octicons-react'
import { Box, Button, Heading, Link, Text } from '@primer/react'

export function SelectHeader() {
    return (
        <Box>
            <Button
                sx={{
                    border: 'none',
                    bg: 'transparent',
                    boxShadow: 'none',
                    display: 'flex',
                    flexDirection: 'row',
                    p: 2,
                    ml: -2,
                    gap: 2,
                }}
            >
                <Heading
                    as="h1"
                    sx={{
                        fontSize: '4',
                        display: 'inline',
                    }}
                >
                    All Usage
                </Heading>
                <TriangleDownIcon size={24} />
            </Button>
            <Text
                sx={{
                    color: 'fg.muted',
                    fontSize: '1',
                }}
            >
                Includes amounts spent for organizations and repositories across
                all services.{' '}
                <Link href="/">
                    <Text
                        as="span"
                        sx={{
                            color: 'accent.fg',
                        }}
                    >
                        View current and past pricing information.
                    </Text>
                </Link>
            </Text>
        </Box>
    )
}
