import { useState } from 'react';
import { DataTable, Table } from '@primer/react/experimental';
import {
  Box,
  FormControl,
  TextInputWithTokens,
  Label,
  LabelGroup,
  Link,
  Pagination,
  RelativeTime,
  Heading,
  Text,
  TextInput,
} from '@primer/react';
import { accessTokens, accessTokens as data } from '../../../data/Data';

export function AccessTokens() {
  const mockTokens = [
    {
      id: '1',
      name: 'Token 1',
    },
    {
      id: '2',
      name: 'Token 2',
    },
  ];

  const [tokens, setTokens] = useState([...mockTokens].slice(0, 3));

  const onTokenRemove = (tokenId) => {
    setTokens(tokens.filter((token) => token.id !== tokenId));
  };

  return (
    <Box sx={{ width: '100%', mt: 5 }}>
      <Box
        sx={{
          pb: 2,
          mb: 3,
          borderBottom: '1px solid',
          borderColor: 'border.default',
          width: '100%',
        }}
      >
        <Heading
          sx={{
            fontSize: 3,
          }}
        >
          Access tokens
        </Heading>
        <Text
          sx={{
            color: 'fg.muted',
            fontSize: 1,
          }}
        >
          View usage by individual token
        </Text>
      </Box>
      <Box>
        <FormControl>
          <FormControl.Label>Default label</FormControl.Label>
          <TextInputWithTokens
            tokens={tokens}
            onTokenRemove={onTokenRemove}
            tokenComponent={({ token }) => (
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  bg: 'bg.default',
                  borderRadius: 2,
                  px: 2,
                  py: 1,
                  mr: 2,
                  mb: 2,
                }}
              >
                <Text>{token}</Text>
              </Box>
            )}
          />
        </FormControl>
      </Box>
      <Table.Container
        sx={{
          width: '100%',
        }}
      >
        <DataTable
          data={accessTokens}
          columns={[
            {
              header: 'Token',
              field: 'name',
              rowHeader: true,
              renderCell: (row) => (
                <Box
                  sx={{
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  <Link
                    href='/token-name'
                    sx={{
                      color: 'fg.default',
                      fontSize: 1,
                    }}
                  >
                    <Text>{row.name}</Text>
                  </Link>
                  <Text
                    sx={{
                      color: 'fg.muted',
                      fontWeight: 400,
                    }}
                  >
                    {row.type}
                  </Text>
                </Box>
              ),
            },
            {
              header: 'Total requests',
              field: 'type',
              renderCell: (row) => <Text>{row.totalRequests}</Text>,
            },
            {
              header: 'Last rate limited',
              field: 'updatedAt',
              renderCell: (row) => <Text>{row.lastUsed}</Text>,
            },
          ]}
        />
      </Table.Container>
      <Box>
        <Pagination
          pageCount={4}
          currentPage={1}
          onPageChange={(e) => e.preventDefault()}
          showPages={{
            narrow: false,
          }}
        />
      </Box>
      {/* <Box sx={{ height: '100px' }} /> */}
    </Box>
  );
}
