import { useState, useRef } from 'react';
import { DataTable, Table } from '@primer/react/experimental';
import { requestLog } from '../../../data/Data';
import { FilterIcon, XCircleFillIcon } from '@primer/octicons-react';
import {
  Box,
  Dialog,
  Link,
  Pagination,
  Heading,
  Text,
  TextInput,
  Button,
} from '@primer/react';

export function RequestLog() {
  const title = 'Request log';
  const description = 'View usage by individual request';

  const [isOpen, setIsOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const returnFocusRef = useRef(null);

  const openModal = (row) => {
    setSelectedRow(row);
    setIsOpen(true);
  };

  const closeModal = () => {
    setSelectedRow(null);
    setIsOpen(false);
  };

  return (
    <Box sx={{ width: '100%', mt: 5 }}>
      <Box>
        <Heading>{title}</Heading>
        <Text>{description}</Text>
      </Box>
      <Table.Container
        sx={{
          width: '100%',
        }}
      >
        <DataTable
          data={requestLog}
          columns={[
            {
              header: 'Request',
              field: 'name',
              rowHeader: true,
              renderCell: (row) => (
                <Box
                  sx={{
                    display: 'flex',
                    flexDirection: 'column',
                  }}
                >
                  <Link
                    data-testid='trigger-button'
                    ref={returnFocusRef}
                    onClick={() => openModal(row)}
                    sx={{
                      color: 'fg.default',
                      fontSize: 1,
                      cursor: 'pointer',
                    }}
                  >
                    <Text mr='2'>{row.type}</Text>
                    <Text fontWeight='normal'>{row.path}</Text>
                  </Link>
                  <Text
                    sx={{
                      color: 'fg.muted',
                      fontWeight: 400,
                    }}
                  >
                    {row.ip}
                  </Text>
                </Box>
              ),
            },
            // ...
          ]}
        />
      </Table.Container>
      {selectedRow && (
        <Dialog
          returnFocusRef={returnFocusRef}
          isOpen={isOpen}
          onDismiss={closeModal}
          aria-labelledby='header'
          sx={{
            borderRadius: '12px',
            overflow: 'hidden',
          }}
        >
          <div data-testid='inner'>
            <Dialog.Header
              id='header'
              sx={{
                bg: 'canvas.default',
              }}
            >
              Request summary
            </Dialog.Header>
            <Box
              fontSize='1'
              p={3}
              sx={{
                display: 'flex',
                flexDirection: 'column',
                gap: 2,
              }}
            >
              <Box sx={{ display: 'flex' }}>
                <Text sx={{ width: '100px' }}>Path</Text>
                <Text sx={{ fontWeight: 'normal' }}>{selectedRow.path}</Text>
              </Box>
              <Box sx={{ display: 'flex' }}>
                <Text sx={{ width: '100px' }}>Status</Text>
                <Text sx={{ fontWeight: 'normal' }}>{selectedRow.type}</Text>
              </Box>
              {/* ... */}
            </Box>
          </div>
          <Box
            sx={{
              borderTop: '1px solid',
              borderColor: 'border.default',
              p: 3,
              display: 'flex',
              justifyContent: 'flex-end',
            }}
          >
            <Button onClick={closeModal}>Close</Button>
          </Box>
        </Dialog>
      )}
      {/* ... */}
    </Box>
  );
}
