import { Box, Heading, Text, TextInput } from '@primer/react';
import { XCircleFillIcon } from '@primer/octicons-react';
import { Filters } from '../../Filters/Filters';

export function FilterBar(title, description) {
  return (
    <>
      <Box
        sx={{
          pb: 2,
          mb: 3,
          borderBottom: '1px solid',
          borderColor: 'border.default',
          width: '100%',
        }}
      >
        <Heading
          sx={{
            fontSize: 3,
          }}
        >
          {title}
        </Heading>
        <Text
          sx={{
            color: 'fg.muted',
            fontSize: 1,
          }}
        >
          {description}
        </Text>
      </Box>
      <Box
        sx={{
          display: 'flex',
          width: '100%',
        }}
      >
        <Filters />
        <Box
          sx={{
            position: 'relative',
            mb: 3,
            width: '100%',
          }}
        >
          <TextInput
            sx={{
              width: '100%',
              borderTopLeftRadius: 0,
              borderBottomLeftRadius: 0,
              pl: '196px',
            }}
            trailingAction={
              <TextInput.Action
                icon={XCircleFillIcon}
                aria-label='Clear input'
                sx={{ color: 'fg.subtle' }}
              />
            }
          />
          <Box
            sx={{
              position: 'absolute',
              left: 2,
              top: '50%',
              transform: 'translateY(-50%)',
              fontSize: 1,
              display: 'flex',
            }}
          >
            <Text>rate limited:</Text>
            <Text
              sx={{
                bg: 'accent.subtle',
                borderRadius: 1,
                px: 1,
                color: 'accent.emphasis',
              }}
            >
              true
            </Text>
          </Box>
        </Box>
      </Box>
    </>
  );
}
