import { useState } from 'react'
import {
    CheckIcon,
    SearchIcon,
    TriangleDownIcon,
    XIcon,
} from '@primer/octicons-react'
import {
    Box,
    Button,
    Heading,
    IconButton,
    Link,
    NavList,
    Text,
    TextInput,
} from '@primer/react'

export function SelectHeader() {
    const [selected, setSelected] = useState('All Usage')
    const [isOpen, setIsOpen] = useState(false)

    const costCenters = [
        'All Usage',
        'GitHub Actions',
        'GitHub Packages',
        'GitHub Sponsors',
        'GitHub Marketplace',
    ]

    return (
        <Box>
            <button
                onClick={() => setIsOpen(!isOpen)}
                style={{
                    border: 'none',
                    bg: 'transparent',
                    boxShadow: 'none',
                    display: 'flex',
                    flexDirection: 'row',
                    px: 2,
                    ml: -2,
                    alignItems: 'center',
                    span: {
                        display: 'flex',
                        alignItems: 'center',
                        gap: 1,
                    },
                    _hover: {
                        bg: 'transparent',
                    },
                    _focus: {
                        bg: 'transparent',
                    },
                }}
            >
                <Heading
                    as="h1"
                    sx={{
                        fontSize: '4',
                        display: 'inline',
                    }}
                >
                    {selected}
                </Heading>
                <TriangleDownIcon size={20} />
            </button>
            {isOpen && (
                <Box
                    sx={{
                        mt: 1,
                        position: 'absolute',
                        bg: 'canvas.default',
                        minWidth: '320px',
                        fontSize: 1,
                        boxShadow:
                            '0 1px 3px 0 rgba(31, 35, 40, 0.12), 0 8px 24px 0 rgba(66, 74, 83, 0.12)',
                        borderRadius: 12,
                    }}
                >
                    <Box
                        sx={{
                            p: 3,
                            pb: 2,
                        }}
                    >
                        <Heading sx={{ fontSize: 1 }}>
                            Select cost center
                        </Heading>
                        <Text
                            sx={{
                                color: 'fg.muted',
                                fontSize: '12px',
                            }}
                        >
                            View usage for individual cost centers
                        </Text>
                        <IconButton
                            onClick={() => setIsOpen(false)}
                            icon={XIcon}
                            sx={{
                                color: 'fg.muted',
                                bg: 'transparent',
                                border: 'none',
                                boxShadow: 'none',
                                position: 'absolute',
                                top: 2,
                                right: 2,
                            }}
                        />
                    </Box>
                    <Box
                        sx={{
                            p: 2,
                            pt: 0,
                            borderBottom: '1px solid',
                            borderColor: 'border.default',
                        }}
                    >
                        <TextInput
                            leadingVisual={SearchIcon}
                            sx={{
                                width: '100%',
                                color: 'fg.muted',
                            }}
                            placeholder="Search"
                        />
                    </Box>
                    <Box sx={{}}>
                        <NavList>
                            {costCenters.map((center) => (
                                <NavList.Item
                                    onClick={() => {
                                        setSelected(center)
                                        setIsOpen(false)
                                    }}
                                    key={center}
                                >
                                    <NavList.LeadingVisual
                                        sx={{
                                            opacity:
                                                center === selected ? '1' : '0',
                                        }}
                                    >
                                        <CheckIcon size={16} />
                                    </NavList.LeadingVisual>
                                    {center}
                                </NavList.Item>
                            ))}
                        </NavList>
                    </Box>
                </Box>
            )}
            <Text
                sx={{
                    color: 'fg.muted',
                    fontSize: '1',
                }}
            >
                Includes amounts spent for organizations and repositories across
                all services.{' '}
                <Link href="/">
                    <Text
                        as="span"
                        sx={{
                            color: 'accent.fg',
                        }}
                    >
                        View current and past pricing information.
                    </Text>
                </Link>
            </Text>
        </Box>
    )
}
