import { Avatar, Box, Heading, NavList, Text } from '@primer/react';
import {
  ChecklistIcon,
  CreditCardIcon,
  GearIcon,
  GitPullRequestIcon,
  HomeIcon,
  IssueOpenedIcon,
  LawIcon,
  OrganizationIcon,
  PersonIcon,
  PlugIcon,
  ShieldIcon,
} from '@primer/octicons-react';

export function SideNav() {
  const nav = [
    {
      icon: <HomeIcon />,
      text: 'Dependencies',
      selected: false,
    },
    {
      icon: <OrganizationIcon />,
      text: 'API',
      selected: true,
    },
    {
      icon: <PersonIcon />,
      text: 'Actions',
      selected: false,
    },
  ];

  return (
    <Box
      sx={{
        display: ['none', 'block'],
        bg: 'canvas.default',
        borderRight: '1px solid',
        borderColor: 'border.default',
        p: 2,
        pt: 4,
        boxSizing: 'border-box',
      }}
    >
      <Heading
        sx={{
          fontSize: 3,
          px: 3,
          py: 1,
        }}
      >
        Insights
      </Heading>
      <NavList
        sx={{
          width: '300px',
          height: '100%',
        }}
        style={{
          marginTop: '-8px',
        }}
      >
        {nav.map((item, index) => (
          <NavList.Item
            key={index}
            href='/'
            selected={item.selected}
          >
            <NavList.LeadingVisual sx={{ color: 'fg.default' }}>
              {item.icon}
            </NavList.LeadingVisual>
            {item.text}
          </NavList.Item>
        ))}
      </NavList>
    </Box>
  );
}
