import { useState } from 'react'
import { TriangleDownIcon } from '@primer/octicons-react'
import { Box, Button, Heading, Link, Text } from '@primer/react'

export function SelectHeader() {
    const [selected, setSelected] = useState('All Usage')
    const [isOpen, setIsOpen] = useState(false)

    return (
        <Box>
            <Button
                onClick={() => setIsOpen(!isOpen)}
                sx={{
                    border: 'none',
                    bg: 'transparent',
                    boxShadow: 'none',
                    display: 'flex',
                    flexDirection: 'row',
                    px: 2,
                    ml: -2,
                    alignItems: 'center',
                    span: {
                        display: 'flex',
                        alignItems: 'center',
                        gap: 1,
                    },
                }}
            >
                <Heading
                    as="h1"
                    sx={{
                        fontSize: '4',
                        display: 'inline',
                    }}
                >
                    All Usage
                </Heading>
                <TriangleDownIcon size={20} />
            </Button>
            {isOpen && (
                <Box
                    sx={{
                        mt: 1,
                        position: 'absolute',
                        p: 2,
                        bg: 'red',
                    }}
                >
                    Dropdown content
                </Box>
            )}
            <Text
                sx={{
                    color: 'fg.muted',
                    fontSize: '1',
                }}
            >
                Includes amounts spent for organizations and repositories across
                all services.{' '}
                <Link href="/">
                    <Text
                        as="span"
                        sx={{
                            color: 'accent.fg',
                        }}
                    >
                        View current and past pricing information.
                    </Text>
                </Link>
            </Text>
        </Box>
    )
}
