import { useState } from 'react';
import {
  CheckIcon,
  CopilotIcon,
  KebabHorizontalIcon,
  SearchIcon,
  TriangleDownIcon,
  XIcon,
} from '@primer/octicons-react';
import {
  Box,
  Button,
  Heading,
  IconButton,
  Link,
  NavList,
  Text,
  TextInput,
} from '@primer/react';

export function Copilot() {
  const [isLoading, setIsLoading] = useState(false);

  return (
    <Box
      sx={{
        width: '100%',
        border: '1px solid',
        borderColor: 'border.default',
        borderRadius: 2,
      }}
    >
      <Box
        sx={{
          display: 'flex',
          gap: 3,
        }}
      >
        <Box
          sx={{
            p: 2,
            border: '1px solid',
            borderColor: 'border.default',
            display: 'inline',
            borderRadius: 2,
            height: 32,
            width: 32,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <CopilotIcon />
        </Box>
        <Heading
          as='h2'
          sx={{
            fontSize: 3,
            fontWeight: 400,
            width: '100%',
          }}
        >
          Copilot
        </Heading>
        <IconButton
          icon={KebabHorizontalIcon}
          variant='invisible'
        />
      </Box>
    </Box>
  );
}
