import { TriangleDownIcon } from '@primer/octicons-react';
import {
  ActionMenu,
  ActionList,
  Box,
  Button,
  Heading,
  Link,
  Text,
} from '@primer/react';
import { timePeriods, timeIncrements } from '../../data/Data';
import { useLocation } from 'react-router-dom';

export function Header() {
  const location = useLocation().pathname;
  const [selectedPeriod, setPeriod] = 0;
  const [selectedIncrement, setIncrement] = 0;

  return (
    <Box
      sx={{
        pb: 2,
        borderBottom: '1px solid',
        borderColor: 'border.default',
        width: '100%',
        alignSelf: 'flex-start',
        display: 'flex',
        gap: 2,
        mb: 3,
      }}
    >
      <Box sx={{ width: '100%' }}>
        <Heading sx={{ fontSize: 3, display: 'inline' }}>
          {location === '/' && 'API'}
          {location !== '/' && <Link href='/'>API</Link>}
          {location === '/' ? null : (
            <>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                  mx: 2,
                }}
              >
                /
              </Text>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                }}
              >
                {location.split('/')[1]}
              </Text>
            </>
          )}
        </Heading>
      </Box>
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>Period:</Box>{' '}
          {selectedPeriod}
        </ActionMenu.Button>{' '}
        <ActionMenu.Overlay align='end'>
          <ActionList>
            {timePeriods.map((period, index) => (
              <ActionList.Item key={index}>{period}</ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>
            Increment:
          </Box>{' '}
          {timeIncrements[selectedIncrement]}}
        </ActionMenu.Button>
        <ActionMenu.Overlay align='end'>
          <ActionList>
            {timeIncrements.map((period, index) => (
              <ActionList.Item
                key={index}
                onSelect={() => alert(`${period} clicked`)}
              >
                {period}
              </ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
    </Box>
  );
}
