import { Box, Text } from '@primer/react';

export function TrendCard({ title, data, description }) {
  return (
    <Box
      sx={{
        p: 3,
        borderRadius: 2,
        boxShadow: 1,
        mb: 3,
        border: '1px solid',
        borderColor: 'border.default',
        width: '100%',
      }}
    >
      {data.map((item, index) => (
        <Box
          key={index}
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            mb: 2,
          }}
        >
          <Box>
            <Text
              sx={{
                fontSize: 1,
                fontWeight: 'bold',
              }}
            >
              {/* {item.value} */}
            </Text>
            <Text
              sx={{
                fontSize: 2,
              }}
            >
              {/* {item.count} */}
            </Text>
            <Text
              sx={{
                fontSize: 0,
              }}
            >
              {item.description}
            </Text>
          </Box>
          <Box
            sx={{
              width: '50%',
            }}
          ></Box>
        </Box>
      ))}
    </Box>
  );
}
