import { useState } from 'react';
import {
  ActionMenu,
  ActionList,
  Box,
  Button,
  Heading,
  Link,
  Text,
} from '@primer/react';
import { timePeriods, timeIncrements } from '../../data/Data';
import { useLocation } from 'react-router-dom';
import { UpgradeDialog } from '../UpgradeDialog/UpgradeDialog';

export function Header() {
  const location = useLocation().pathname;
  const [selectedIndex, setSelectedIndex] = useState(2);
  const [selectedIncrementIndex, setSelectedIncrementIndex] = useState(3);
  const timePeriod = [
    {
      type: 'Last hour',
    },
    {
      type: 'Last 3 hours',
    },
    {
      type: 'Last 12 hours',
    },
    {
      type: 'Last 24 hours',
    },
    {
      type: 'Last week',
    },
    {
      type: 'Last month',
    },
  ];

  return (
    <Box
      sx={{
        pb: 2,
        borderBottom: '1px solid',
        borderColor: 'border.default',
        width: '100%',
        alignSelf: 'flex-start',
        display: 'flex',
        flexDirection: ['column', null, 'row'],
        gap: 2,
        mb: 3,
      }}
    >
      <Box sx={{ width: '100%' }}>
        <Heading sx={{ fontSize: 3, display: 'inline' }}>
          {location === '/' ? 'API' : <Link href='/'>API</Link>}
          {location === '/' ? null : (
            <>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                  mx: 2,
                }}
              >
                /
              </Text>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                }}
              >
                {location.split('/')[1]}
              </Text>
            </>
          )}
        </Heading>
      </Box>
      {/* <UpgradeDialog /> */}
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>Period:</Box>{' '}
          {timePeriod[selectedIndex].type}
        </ActionMenu.Button>{' '}
        <ActionMenu.Overlay align='end'>
          <ActionList selectionVariant='single'>
            {timePeriods.map((period, index) => (
              <ActionList.Item
                key={index}
                selected={index === selectedIndex}
                onSelect={() => setSelectedIndex(index)}
              >
                {period.type}
              </ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>
            Increment:
          </Box>{' '}
          {timeIncrements[selectedIncrementIndex].type}
        </ActionMenu.Button>
        <ActionMenu.Overlay align='end'>
          <ActionList selectionVariant='single'>
            {timeIncrements.map((period, index) => (
              <ActionList.Item
                key={index}
                selected={index === selectedIncrementIndex}
                onSelect={() => setSelectedIncrementIndex(index)}
              >
                {period.type}
              </ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
    </Box>
  );
}
