import { Box, Heading, Text } from '@primer/react';

export function DataCard({ data, description, title }) {
  const totalRequests = data.reduce((total, request) => total + request, 0);

  return (
    <Box
      sx={{
        p: 3,
        borderRadius: 2,
        boxShadow: 1,
        mb: 3,
        border: '1px solid',
        borderColor: 'border.default',
        width: '100%',
      }}
    >
      <Heading
        sx={{
          fontSize: 1,
        }}
      >
        {title}
      </Heading>
      <Text
        sx={{
          fontSize: 4,
          display: 'block',
        }}
      >
        {totalRequests}
      </Text>
      <Text
        sx={{
          color: 'fg.muted',
          fontSize: 1,
        }}
      >
        {description}
      </Text>
    </Box>
  );
}
