import { Avatar, Box, Heading, NavList, Text } from '@primer/react'
import {
    GitPullRequestIcon,
    HomeIcon,
    IssueOpenedIcon,
} from '@primer/octicons-react'

export function SideNav() {
    const style = {
        bg: 'canvas.subtle',
        borderBottom: '1px solid',
        borderColor: 'border.default',
        borderRadius: 0,
        p: 3,
        width: '100%',
        ml: -2,
        mr: -2,
    }
    return (
        <Box
            sx={{
                bg: 'canvas.default',
                borderRight: '1px solid',
                borderColor: 'border.default',
            }}
        >
            <Box
                sx={{
                    p: 3,
                    borderBottom: '1px solid',
                    borderColor: 'border.default',
                }}
            >
                <Box
                    sx={{
                        display: 'flex',
                        gap: '3',
                        alignItems: 'center',
                    }}
                >
                    <Avatar
                        size={24}
                        src="https://avatars.githubusercontent.com/b/2?s=48&v=4"
                    />
                    <Heading
                        sx={{
                            fontSize: 3,
                            fontWeight: '400',
                        }}
                    >
                        Avocado Corp.
                    </Heading>
                </Box>
                <Text
                    sx={{
                        fontSize: 1,
                    }}
                >
                    Makers of the best avocados.
                </Text>
            </Box>
            <NavList
                sx={{
                    width: '300px',
                    height: '100%',
                    pt: -2,
                }}
            >
                <NavList.Item sx={style} href="/" aria-current="page">
                    <NavList.LeadingVisual>
                        <HomeIcon />
                    </NavList.LeadingVisual>
                    Dashboard
                </NavList.Item>
                <NavList.Item sx={style} href="/pulls">
                    <NavList.LeadingVisual>
                        <GitPullRequestIcon />
                    </NavList.LeadingVisual>
                    Pull requests
                </NavList.Item>
                <NavList.Item sx={style} href="/issues">
                    <NavList.LeadingVisual>
                        <IssueOpenedIcon />
                    </NavList.LeadingVisual>
                    Issues
                </NavList.Item>
            </NavList>
        </Box>
    )
}
