import { Box, Heading, Text } from '@primer/react';

export function TrendCard({ title, data, description }) {
  return (
    <Box
      sx={{
        p: 3,
        borderRadius: 2,
        boxShadow: 1,
        mb: 3,
        border: '1px solid',
        borderColor: 'border.default',
        width: '100%',
      }}
    >
      <Box
        sx={{
          mb: 3,
        }}
      >
        <Heading
          sx={{
            fontSize: 2,
          }}
        >
          {title}
        </Heading>
        <Text
          sx={{
            fontSize: 1,
            color: 'fg.muted',
          }}
        >
          {description}
        </Text>
      </Box>
      {data.map((item, index) => (
        <Box
          key={index}
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            // bg: 'pink',
            py: 2,
            borderTop: '1px solid',
            borderColor: 'border.default',
          }}
        >
          <Text
            sx={{
              fontSize: 1,
            }}
          >
            {item.value}
          </Text>
          <Text>{item.count}</Text>
        </Box>
      ))}
    </Box>
  );
}
