import { Box } from '@primer/react';
import React from 'react';
import { Line } from 'react-chartjs-2';
import { chart } from '../../../data/Data';

export function LineChart() {
  return (
    <Box
      sx={{
        position: 'relative',
        height: '320px',
        width: '100%',
      }}
    >
      <Line
        data={chart}
        options={{
          maintainAspectRatio: false,
          height: '340px',
          responsive: true,
          datasets: [
            {
              label: 'Requests',
              fill: true,
              backgroundColor: 'rgba(45, 164, 78, 0.1)',
              borderColor: '#2DA44E',
              borderWidth: 1,
            },
          ],
          plugins: {
            title: {
              display: false,
              text: 'Total requests made by this organization',
            },
            legend: {
              display: false,
            },
          },
          elements: {
            point: {
              radius: 0,
            },
          },
        }}
      />
    </Box>
  );
}
