import { useState } from 'react';
import {
  ActionMenu,
  ActionList,
  Box,
  Button,
  Heading,
  Link,
  Text,
} from '@primer/react';
import { timePeriods, timeIncrements } from '../../data/Data';
import { useLocation } from 'react-router-dom';

export function Header() {
  const location = useLocation().pathname;
  const [timePeriod, setTimePeriod] = useState([
    {
      type: 'Last hour',
      selected: false,
    },
    {
      type: 'Last 3 hours',
      selected: false,
    },
    {
      type: 'Last 12 hours',
      selected: true,
    },
    {
      type: 'Last 24 hours',
      selected: false,
    },
    {
      type: 'Last week',
      selected: false,
    },
    {
      type: 'Last month',
      selected: false,
    },
  ]);

  return (
    <Box
      sx={{
        pb: 2,
        borderBottom: '1px solid',
        borderColor: 'border.default',
        width: '100%',
        alignSelf: 'flex-start',
        display: 'flex',
        gap: 2,
        mb: 3,
      }}
    >
      <Box sx={{ width: '100%' }}>
        <Heading sx={{ fontSize: 3, display: 'inline' }}>
          {location === '/' ? 'API' : <Link href='/'>API</Link>}
          {location === '/' ? null : (
            <>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                  mx: 2,
                }}
              >
                /
              </Text>
              <Text
                sx={{
                  display: 'inline',
                  fontSize: 'inherit',
                  fontSize: 'inherit',
                }}
              >
                {location.split('/')[1]}
              </Text>
            </>
          )}
        </Heading>
      </Box>
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>Period:</Box>{' '}
          Last 24 hours
        </ActionMenu.Button>{' '}
        <ActionMenu.Overlay align='end'>
          <ActionList selectionVariant='single'>
            {timePeriods.map((period, index) => (
              <ActionList.Item
                key={index}
                selected={period.selected}
                onSelect={
                  period.selected
                    ? () => null
                    : () => setTimePeriod(period.selected)
                }
              >
                {period.type}
              </ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
      <ActionMenu>
        <ActionMenu.Button>
          <Box sx={{ color: 'fg.muted', display: 'inline-block' }}>
            Increment:
          </Box>{' '}
          1 hour
        </ActionMenu.Button>
        <ActionMenu.Overlay align='end'>
          <ActionList selectionVariant='single'>
            {timeIncrements.map((period, index) => (
              <ActionList.Item
                key={index}
                selected={period.selected}
              >
                {period.type}
              </ActionList.Item>
            ))}
            <ActionList.Divider />
            <ActionList.Item onSelect={() => alert('Delete file clicked')}>
              Custom time range
            </ActionList.Item>
          </ActionList>
        </ActionMenu.Overlay>
      </ActionMenu>
    </Box>
  );
}
