import { Box } from '@primer/react';
import React from 'react';
import { Line } from 'react-chartjs-2';

export function LineChart({ data }) {
  return (
    <Box
      sx={{
        position: 'relative',
        height: '320px',
        width: '100%',
      }}
    >
      <Line
        data={data}
        options={{
          maintainAspectRatio: false,
          height: '340px',
          responsive: true,
          plugins: {
            title: {
              display: false,
              text: 'Total requests made by this organization',
            },
            legend: {
              display: false,
            },
          },
        }}
      />
    </Box>
  );
}
