import { useState, useRef } from 'react';
import { Dialog, Button, Box, Text } from '@primer/react';
import {
  AlertIcon,
  AppsIcon,
  FilterIcon,
  KeyAsteriskIcon,
  PersonIcon,
  RelFilePathIcon,
  RepoIcon,
} from '@primer/octicons-react';

export function Filters() {
  const [isOpen, setIsOpen] = useState(false);
  const returnFocusRef = useRef(null);

  const filters = [
    {
      name: 'Type',
      options: [
        { label: 'Personal Access Token', value: 'pat' },
        { label: 'GitHub App', value: 'github' },
        { label: 'OAuth App', value: 'oauth' },
      ],
      icon: KeyAsteriskIcon,
      selected: true,
    },
    {
      name: 'App name',
      options: [{ label: 'Option', value: 'option' }],
      icon: AppsIcon,
      selected: false,
    },
    {
      name: 'Username',
      options: [{ label: 'Option', value: 'option' }],
      icon: PersonIcon,
      selected: false,
    },
    {
      name: 'Rate limited',
      options: [{ label: 'True', value: 'option' }],
      icon: AlertIcon,
      selected: true,
    },
    {
      name: 'Route',
      options: [{ label: 'Option', value: 'option' }],
      icon: RelFilePathIcon,
      selected: false,
    },
    {
      name: 'Path',
      options: [{ label: 'Option', value: 'option' }],
      icon: RelFilePathIcon,
      selected: false,
    },
    {
      name: 'Repository',
      options: [{ label: 'Option', value: 'option' }],
      icon: RepoIcon,
      selected: false,
    },
  ];

  return (
    <div>
      <Button
        ref={returnFocusRef}
        onClick={() => setIsOpen(true)}
        count={2}
        sx={{
          borderTopRightRadius: 0,
          borderBottomRightRadius: 0,
          borderRight: 0,
        }}
        leadingVisual={FilterIcon}
      >
        Filter
      </Button>
      <Dialog
        returnFocusRef={returnFocusRef}
        isOpen={isOpen}
        onDismiss={() => setIsOpen(false)}
        aria-labelledby='header'
        sx={{
          width: '640px',
          borderRadius: '12px',
          overflow: 'hidden',
        }}
      >
        <div data-testid='inner'>
          <Dialog.Header
            sx={{ bg: 'canvas.default' }}
            id='header'
          >
            Advanced filters
          </Dialog.Header>
          <Box p={3}>
            <Text>Some content</Text>
          </Box>
        </div>
        <Box
          sx={{
            borderTop: '1px solid',
            borderColor: 'border.default',
            p: 3,
            display: 'flex',
            justifyContent: 'flex-end',
            gap: 2,
          }}
        >
          <Button onClick={() => setIsOpen(false)}>Close</Button>
          <Button
            variant='primary'
            onClick={() => setIsOpen(false)}
          >
            Save
          </Button>
        </Box>
      </Dialog>
    </div>
  );
}
